rs = [1,2,3,4]
if x &amp;gt; 0:
   rs.remove(2)
   rs.remove(3)
   if y &amp;gt; 0:
     rs.remove(4)
   else:
     rs.remove(1)
else:
   rs.remove(1)
   rs.remove(4)
   if y &amp;gt; 0 :
     rs.remove(3)
   else:
     rs.remove(2)
