   ws = wb.active
   title = None
   for index,row in enumerate(ws.rows):
     if index == 0:
       ws.cell(index+1,5).value = &#39;汇总&#39;
   else:
     if row[1].value &amp;lt; 18 and row[3].value &amp;gt;= 6:
       ws.cell(index+1,5).value = 120 - row[2].value
   wb.save(f&#39;test_19.xlsx&#39;)
