&lt;pre&gt;&lt;code&gt;&amp;#123;
    &amp;quot;cmd&amp;quot;: [&amp;quot;gcc&amp;quot;,&amp;quot;$&amp;#123;file&amp;#125;&amp;quot;,&amp;quot;-o&amp;quot;, &amp;quot;$&amp;#123;file_path&amp;#125;/$&amp;#123;file_base_name&amp;#125;&amp;quot;],
